import { useEffect, useRef, useState } from "react";
import FlagGeorgia from "../Ui/Icons/FlagGeorgia";
import "./ProductHeader.css";
import MoreIcon from "../Ui/Icons/MoreIcon";

interface IProdcutHeader {
  info: {
    infoType: string;
    productInfo: string;
  }[];
}

const ProductHeader = ({ info }: IProdcutHeader) => {
  const [active, setActive] = useState<boolean>(false);

  const ref = useRef<any>();

  const viewMore = () => {
    setActive(!active);
  };

  useEffect(() => {
    const ckecker = () =>{
      if(!(window.innerWidth >769)){
        console.log(true)
      }   
    }
    window.addEventListener('resize' ,ckecker )

    return window.removeEventListener('resize' , ckecker)
  }, [ref.current?.style.display]);

  return (
    <div className="product_header" >
      <div className="product_header_main">
        <h3 className="product_title">2014 - BMW 3er Touring</h3>
        <MoreIcon onClick={viewMore} />
      </div>
      <div className={`product_header_Info${active ? " active" : ""}`}  ref={ref}>
        {info.map((el) => {
          return (
            <div key={el.infoType}>
              <p>{el.infoType}:</p>
              <p className="product_header_Info_item">{el.productInfo}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ProductHeader;
