import React, { useEffect, useRef, useState } from "react";
import MyInput from "../../Ui/customUi/MyInput/MyInput";
import MyButton from "../../Ui/customUi/MyButton/MyButton";

const BidInfo = ({ active }: any) => {
  const [isStickyActive, setIsStickyActive] = useState<boolean>(false);
  const stickyEl = useRef<any>(null);
  const relativeEl = useRef<any>(null);

  useEffect(() => {
    var stickyThreshold = relativeEl.offsetTop; // Change this value as needed

    if (window.pageYOffset >= stickyThreshold) {
      // stickyEl.classList.add("sticky");
    } else {
      // stickyEl.classList.remove("sticky");
    }
  }, []);

  return (
    <div className="bid_Information_List" ref={relativeEl}>
      {!active && (
        <>
          <div className="title__Info__Field bid_Information_title">
            Bid information
          </div>

          <div className="car_info_item shadow__Item">
            <p>Bid Status:</p>
          </div>
          <div className="car_info_item shadow__Item">Minimum Bid:</div>
        </>
      )}
      <div
        className={`shadow__Item car_info_item Bid_form${
          isStickyActive ? " sticky" : ""
        }`}
        ref={stickyEl}
      >
        <div className="bid__current_bid">
          <p>Current Bid:</p>
          <p>$0</p>
        </div>
        {!active && (
          <>
            <p>Your Max Bid:</p>
            <div className="bid__buy_item">
              <span>$</span>
              <MyInput placeholder="400" />
            </div>
            <MyButton className={"bid_button"}>Bid Now</MyButton>
            <p className="bid_price" style={{ textAlign: "center" }}>
              $3.400
            </p>
          </>
        )}
        <MyButton className={"bid_button buy_button"}>Buy It Now</MyButton>
      </div>
    </div>
  );
};

export default BidInfo;
