import { NavLink, useLocation } from "react-router-dom";
import "./Header.scss";
import { useCallback, useEffect, useState } from "react";
import {
  NavMenuIcon,
  SelectSvg,
  FlagUS,
  Logo,
  CloseIcon,
} from "../../assets/Icons";

const Header = () => {
  const [active, setActive] = useState(false);
  const { pathname } = useLocation();
  const path = pathname.split("/");

  const clickHandler = useCallback(() => {
    setActive(!active);
  }, [active]);

  useEffect(() => {
    if (active) {
      document.body.style.overflowY = "hidden";
    } else {
      document.body.style.overflowY = "auto";
    }
    const closeSelects = () => {
      setActive(false);
    };

    document.addEventListener("click", closeSelects);

    return () => document.removeEventListener("click", closeSelects);
  }, [active]);

  useEffect(() => {
    function updateSize() {
      if (!(window.innerWidth <= 769) && active) {
        setActive(false);
      }
    }
    updateSize();
    window.addEventListener("resize", updateSize);
    return () => window.removeEventListener("resize", updateSize);
  }, [active, clickHandler]);

  return (
    <header className={`header ${path[path.length - 1]}`}>
      <div className="container">
        <Logo />
        <nav
          onClick={(e) => {
            e.stopPropagation();
          }}
        >
          {!active ? (
            <NavMenuIcon onClick={clickHandler} />
          ) : (
            <CloseIcon onClick={clickHandler} />
          )}

          <ul
            className={`header_nav_ul ${active ? "active" : ""} ${
              path[path.length - 1]
            }`}
          >
            <li>
              <NavLink to={"/"}>Home</NavLink>
            </li>
            <li>
              <NavLink to={"/product"}>
                Auction <SelectSvg />
              </NavLink>
            </li>
            <li>
              <NavLink to={"/product_bid"}>Log In</NavLink>
            </li>
            <li>
              <NavLink to={"/"}>Register</NavLink>
            </li>
            <li>
              <NavLink to={"/"}>
                <FlagUS />
                Eng
                <SelectSvg />
              </NavLink>
            </li>
            <li>
              <NavLink to={"/"}>
                <time>05:07</time>
                <SelectSvg />
              </NavLink>
            </li>
            <li>
              <NavLink to={"/"}>Contac Us</NavLink>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
