import { useEffect, useRef, useState } from "react";
import FlagGeorgia from "../../Ui/Icons/FlagGeorgia";
import "./ProductHeader.css";
import MoreIcon from "../../Ui/Icons/MoreIcon";
import ProductHeaderLinks from "../ProductHeaderLinks/ProductHeaderLinks";

export type IProductHeaderInfo = {
  infoType: string;
  productInfo: string;
};

interface IProdcutHeader {
  info: IProductHeaderInfo[];
  onChange: (e: boolean) => void;
  productIsActive: boolean;
}

const ProductHeader = ({ info, onChange, productIsActive }: IProdcutHeader) => {
  const [active, setActive] = useState<boolean>(false);

  const ref = useRef<any>();

  const viewMore = () => {
    setActive(!active);
  };

  useEffect(() => {
    const updateSize = () => {
      if (!(window.innerWidth > 769) && !productIsActive) {
        onChange(true);
      } else if (!(window.innerWidth <= 769) && productIsActive) {
        onChange(false);
      }
    };
    updateSize();
    window.addEventListener("resize", updateSize);
    return () => window.removeEventListener("resize", updateSize);
  }, [productIsActive, onChange]);

  // @TO-DO use component
  // const renderHeaderLinks = info.map((el) => (
  //   <div key={el.infoType}>
  //     <p>{el.infoType}:</p>
  //     <h6 className="product_header_Info_item">
  //       {el.infoType === "Sale Location" && <FlagGeorgia />}
  //       {el.productInfo}
  //     </h6>
  //   </div>
  // ));

  return (
    <div className="product_header">
      <div className="product_header_main">
        <h3 className="product_title">2014 - BMW 3er Touring</h3>
        <MoreIcon onClick={viewMore} />
      </div>
      <div
        className={`product_header_Info${active ? " active" : ""}`}
        ref={ref}
      >
        {!productIsActive && <ProductHeaderLinks info={info} />}
      </div>
    </div>
  );
};

export default ProductHeader;
